package ru.stqa.pft.addressbook.tests;

import org.junit.Assert;
import org.junit.Test;
import ru.stqa.pft.addressbook.model.ContactData;

import java.util.HashSet;
import java.util.List;

public class ContactCreationTests extends TestBase {

  @Test
  public void profileCreationTests() {
    //int before = app.getContactHelper().getContactCount(); //контролль количества контактов до
    app.getNavigationHelper().goToNewProfile();
    List<ContactData> before = app.getContactHelper().getContactList();
    ContactData contact = new ContactData("test_name", "test_surname", "test1");
    app.getContactHelper().createContact(contact);
    List<ContactData> after = app.getContactHelper().getContactList();
    //int after = app.getContactHelper().getContactCount(); //контролль количества контактов после
    Assert.assertEquals(after.size(), before.size() + 1); //количество контактов увеличилось на 1

    int max = 0;
    for (ContactData g : after) {
      if (g.getId() > max) {
        max = g.getId();
      }
    }
    contact.setId(max);
    before.add(contact);
    Assert.assertEquals(new HashSet<Object>(before), new HashSet<Object>(after));

  }

}
